
function Stream(){
	var buffer = "";
	var del = '\n';

	this.retrieve = function (data){
		var j_data = [];
		buffer += data.toString();
		d_index = buffer.indexOf(del);
		while(d_index > -1){
			var c_data = buffer.substring(0, d_index);
			j_data.push(JSON.parse(c_data));
			buffer = buffer.substring(d_index+1);
			d_index = buffer.indexOf(del);
		}
		return j_data;
	}
}

module.exports = Stream;